import './styles.css';
import Board from "./components/Board";
import {WORDS} from './constants.js';
import {useState} from 'react';

function App() {
  const [reload, setReload] = useState(0);
  const idx = Math.floor((Math.random() * 2314) + 1);

  return (
    <div className="App">
     <Board wordle={WORDS[idx]} reload={reload} ></Board>
     <button onClick={(e) => {e.preventDefault(); setReload(reload + 1)}}>Restart</button>


    </div>
  );
}

export default App;
