import React from 'react';
import {createContext, useState} from 'react'

export const GameContext = createContext();


function GameProvider({children}) {
    const [gameID, setGameID] = useState(null);
    const [clientID, setClientID] = useState(null);
    const [gameStatus, setGameStatus] = useState("none");
    const [createJoin, setCreateJoin] = useState(null)
    const [user, setUser] = useState(null)
  return <GameContext.Provider value={{user, setUser, createJoin, setCreateJoin, gameID, setGameID, gameStatus, setGameStatus, clientID, setClientID}}>{children}</GameContext.Provider>
}

export default GameProvider;
