import './styles.css';
import Board from "./components/Board";
import {WORDS} from './constants.js';
import {useEffect, useState} from 'react';
import {w3cwebsocket as W3CWebSocket} from "websocket";
import GameProvider from "./context/GameContext";


function App() {
  const [message, setMessage] = useState(null)
  const idx = Math.floor((Math.random() * 2314) + 1);
  const client = new W3CWebSocket('ws://localhost:5000');



  const sendMessage = (e) => {
    e.preventDefault()
    client.send(
    JSON.stringify({type: "Hi", "message" : "kys"}))

  }





  return (
    <GameProvider>
    <div className="App">
      {message}
     <Board client={client} wordle={WORDS[idx]} ></Board>
     <button onClick={(e)=> sendMessage(e)}>hi</button>
    </div>
    </GameProvider>
  );
}

export default App;
