import './styles.css';
import Board from "./components/Board";
import {WORDS} from './constants.js';
import {useEffect, useState, useContext} from 'react';
import {w3cwebsocket as W3CWebSocket} from "websocket";
import {GameContext} from './context/GameContext.js'
import JoinRoom from "./components/JoinRoom";

function App() {
  const [message, setMessage] = useState(null)
  const idx = Math.floor((Math.random() * 2314) + 1);
  const client = new W3CWebSocket('ws://localhost:5000');
  const {gameID} = useContext(GameContext)




  const sendMessage = (e) => {
    e.preventDefault()
    client.send(
    JSON.stringify({type: "Hi", "message" : "kys"}))

  }

  return (
    <div className="App">
      {message}
     {gameID && <Board gameID={gameID} client={client} wordle={WORDS[idx]} ></Board>}
     {!gameID && <JoinRoom></JoinRoom>}
     <button onClick={(e)=> sendMessage(e)}>hi</button>
    </div>
  );
}

export default App;
