import { VALIDGUESSES, WORDS } from "../constants";

export function checkKey(e, historyRef, gameStatusRef, currentRowRef, setHistory, setCurrentRow, setWinner, setRematch, setError, wordle, client, clientID, gameID, user) {
    if (historyRef.current.length == 6 || gameStatusRef.current ) return;
    console.log(e.keyCode);
    console.log(e.key);
    
    if (currentRowRef.current.length == 5) {
        
        if (e.keyCode == 13) {
            console.log("entered");
            var word = currentRowRef.current

            console.log(word);
            if (!VALIDGUESSES.includes(word.join('')) && !WORDS.includes(word.join(''))) {
                setError(true);
                setTimeout(() => {
                    setError(false)
                }, 2000)
                return 
            }
       var target = wordle;
       var final = []
       for (var i in word) {
           var status = "gray"
           if (word[i] == target[i]) {
               status = "green"
               target = target.replace(word[i], " ")
           } else if (target.includes(word[i])) {
               status = "yellow"
               target = target.replace(word[i], " ")
           } 
           final.push({key: word[i], status: status});
          
       }
       setHistory([...historyRef.current, final])
       setCurrentRow([])
     
    //    dispatch({type: actionTypes.ACCEPT_RESPONSE, payload: final})
       client.send(JSON.stringify({type: "update", payload: {rows: historyRef.current, clientID: clientID, gameID: gameID}}));
    //    setCurrentRow([])
       if (final.every((value) => value.status == "green")) {
        const data = {
            type: "winner",
            payload: {
                winner: user,
                gameID: gameID
                }
        }
        client.send(JSON.stringify(data));
        setWinner(true);
        setRematch(true);
     }
        } 
        else if (e.keyCode == 8) {
            const tmp = currentRowRef.current.slice(0, currentRowRef.current.length - 1)
            setCurrentRow(tmp)
        }
    } else if (e.keyCode == 8) {
        const tmp = currentRowRef.current.slice(0, currentRowRef.current.length - 1)
        setCurrentRow(tmp)
    } else if (currentRowRef.current.length < 5) {
        if (e.keyCode >= 65 && e.keyCode <= 90) {
            setCurrentRow([...currentRowRef.current, e.key])
        }

    }
}

export function resetKeys(dirtyKeys) {
    for (var i in dirtyKeys) {
        for (var j in dirtyKeys[i]) {
            dirtyKeys[i][j].status = "none";
        }
    }
    return dirtyKeys;
}

export function colourKeys(initKeysOne, history) {
    for (var a in history) {
        for (var c in history[a]) {
            var flag = false
            if (flag) break;
            else {
                for (const j in initKeysOne) {
                    for (var b in initKeysOne[j]) {
                        if (history[a][c].key == initKeysOne[j][b].key) {
                            initKeysOne[j][b].status = history[a][c].status
                            flag = true;
                            break;
                        }
                    }
                }
                
            }
            
        }
    }
    return initKeysOne
}
