import React from 'react';
import HistoryRow from "./HistoryRow";
import {useState, useEffect} from 'react'
import CurrentRow from "./CurrentRow";
import BlankRows from "./BlankRows";
import Opponent from "./Opponent";
import {keyrow1, keyrow2, keyrow3} from '../constants.js';
import {checkKey, resetKeys, colourKeys} from '../functions/handler.js'
import {useStateRef} from '../hooks/index.js';

function Board({gameID, wordle, client, players, user, gameEnd, reload, clientID, opponentData, keysa}) {
   const [error, setError] = useState(false)
   const [winner, setWinner] = useState(null);
   const [keyboard, setKeyboard] = useState([keyrow1, keyrow2, keyrow3])
   const [rematch, setRematch] = useState(false);
   const [tie, setTie] = useState(false)
  
   const [gameStatus, gameStatusRef, setGameStatus] = useStateRef(false);
   const [currentRow, currentRowRef, setCurrentRow] = useStateRef([])
   const [history, historyRef, setHistory] = useStateRef([])

useEffect(() => {
    if (gameEnd.status) {
        setGameStatus(true);
    } else {
        setGameStatus(false);
    }
}, [gameEnd])

useEffect(() => {
    const dirty = [keyrow1, keyrow2, keyrow3];
    var newKeys = resetKeys(dirty);
    if (!gameStatusRef.current) {
        setTimeout(() => {
            setKeyboard([...newKeys, [Math.random()]])
        }, 100)
    }
    setHistory([])
    setCurrentRow([]);
    setWinner(null);
    setTie(false)
    window.addEventListener('keyup', middlewareKey);
    
    return () => {
        window.removeEventListener('keyup', middlewareKey)
    }
}, [wordle])

   useEffect(() => {
    if (history.length > 0) {
        const initKeys1 = [keyrow1, keyrow2, keyrow3];
        var initKeysOne = colourKeys(initKeys1, history);
    setKeyboard(initKeysOne)
    }
}, [history])
   
   const handleRematch = e => {
       e.preventDefault();
       const data = {
           type: "rematch",
           payload: {
                gameID: gameID,
                clientID: clientID,
           }
           
       }
       client.send(JSON.stringify(data));
       setRematch(false);
      
       
   }
    function middlewareKey(e) {
    checkKey(e, historyRef, gameStatusRef, currentRowRef, setHistory, setCurrentRow, setWinner, setRematch, setError, wordle, client, clientID, gameID, user)
}

    const handleKeyChange = (d) => {
        d.preventDefault();
        if (historyRef.current.length == 6 || gameStatusRef.current) return;
        const key = d.target.getAttribute("data")
        const keyCode = d.target.getAttribute("code")
        middlewareKey({key: key, keyCode: keyCode});
    }
    
    useEffect(()=>{
        if (gameStatusRef.current) {
            if (gameEnd.winner == user) {
                setWinner(true)
            } else if (gameEnd.tie) {
                setTie(true)
            } else {
                setWinner(false)
            }
            setRematch(true);
        }
    }, [reload])

  return (<div className="board-container">
    <Opponent opponentData={opponentData} opponent={players.find((player) => player !== user)}></Opponent>
    <div className="user-container">
        <div className="toasty">
            <div className={error ? "toast-container error" : "toast-container"}>Not a word!</div>
            <div className={winner ? "toast-container error" : "toast-container"}>You won!</div>
            <div className={winner == false ? "toast-container error" : "toast-container"}>You lost!</div>
            <div className={tie ? "toast-container error" : "toast-container"}>Tie!</div>
            <div onClick={(e) => handleRematch(e)}className={rematch ? "toast-container rematch" : "toast-container"}>Rematch</div>
        </div>
      <p>{user}</p>
        {history.length > 0 && <HistoryRow history={historyRef.current} opponent={"false"}></HistoryRow>}
        {history.length < 6 && <div className="currentrow-container"> <CurrentRow currentRow={currentRowRef.current}></CurrentRow>
        <BlankRows opponent={"false"} blankRows={6 - historyRef.current.length - 1}></BlankRows></div>}
      <input className="fallback-input" ></input>
      </div> 
    <div className="keyboard">
        <div className="keyrow one">
            {keyboard[0].map((key) => {
                return (
                    <div  value={key.key} className={`keytile ${key.status}`}>
                        <button onClick={(e) => handleKeyChange(e)} data={key.key} code={key.key.toUpperCase().charCodeAt(0)} className={`keyboardtile ${key.status}`}>{key.key.toUpperCase()}</button>
                    </div>
                )
            })}
        </div>
        <div className="keyrow two">
            {keyboard[1].map((key) => {
                return (
                    <div  value={key.key} className={`keytile ${key.status}`}>
                        <button onClick={(e) => handleKeyChange(e)} data={key.key} code={key.key.toUpperCase().charCodeAt(0)} className={`keyboardtile ${key.status}`}>{key.key.toUpperCase()}</button>
                    </div>
                )
            })}
        </div>
        <div className="keyrow three">
            <div className="keytile none special"  value={"ENTER"}>
                        <button onClick={(e) => handleKeyChange(e)} data={13} code={13} className={`keyboardtile`}>ENTER</button>
                    </div>
            {keyboard[2].map((key) => {
                
                return (
                    <div value={key.key}  className={`keytile ${key.status}`}>
                        <button  onClick={(e) => handleKeyChange(e)} data={key.key} code={key.key.toUpperCase().charCodeAt(0)} className={`keyboardtile ${key.status}`}>{key.key.toUpperCase()}</button>
                    </div>
                )
            })}
            <div value={"DELETE"} className="keytile none special">
            <button onClick={(e) => handleKeyChange(e)} data={8} code={8} className={`keyboardtile`}>BACK</button>
         </div>
    </div>
</div>

  </div>)
}

export default Board;
