import React, { useEffect } from 'react';
import { GameContext } from "../context/GameContext";
import {useContext, useState} from 'react'
function JoinRoom({client}) {
  const {setGameID, clientID, gameID, createJoin, setCreateJoin} = useContext(GameContext);
  const [inviteCode, setInviteCode] = useState("")
 

  const handleCreateRoom = (e) => {
    e.preventDefault()
    const data = {
      type: "create",
      payload: {clientID: clientID}
    }
    client.send(JSON.stringify(data))
    
  }

  const handleChange = (e) => {
    e.preventDefault();
    setInviteCode(e.target.value)
    
  } 

  const handleExitRoom = (e) => {
    e.preventDefault();
    const data = {
      type: "exit",
      payload: {gameID: gameID}

    }
    client.send(JSON.stringify(data));
    setCreateJoin(null);
    setGameID(null);
  }

  return <div className="joinroom-container">
    <h2>Multiplayer Wordle by Si Wei</h2>
    <div className="option-container">
      {!createJoin && <div className="buttons">
          <button onClick={(e) => handleCreateRoom(e)}>Create Room</button>
          <button onClick={(e) => {e.preventDefault(); setCreateJoin("join")}}>Join Room</button>
        </div>}
      {createJoin == "create" && <div className="invite-code-container">
        <p>Room Created. Send your friend this invite code!</p>
          <div className="invite-code">{gameID}</div>
          <button onClick={(e) => handleExitRoom(e)}>Exit Room</button>
        </div>}
      {createJoin == "join" && <div className="invite-code-container">
        <p>Enter your invite code</p>
        <input onChange={(e) => handleChange(e)}></input>
        <button>Submit</button>
        </div>}
    </div>
  </div>;
}

export default JoinRoom;
