import React from 'react';
import HistoryRow from "./HistoryRow";
import {useState, useEffect, useCallback, useReducer, useRef} from 'react'
import CurrentRow from "./CurrentRow";
import BlankRows from "./BlankRows";
function Board({wordle}) {
    console.log(wordle);
   const [history, setHistory] = useState([])
   const [currentRow, setCurrentRow] = useState([]);
   const [reload, setReload] = useState(0)
   const [counter, setCounter] = useState(0)
   const testRef = useRef(null)

   var gameEnd = false
   var prevRow = []
   var prevHistory = []
   prevHistory = [];
  
   function checkKey(e) {
       
    if (prevHistory.length == 6 || gameEnd) return;
    if (prevRow.length == 5) {
        if (e.keyCode == 13) {
            var word = prevRow
       var target = wordle;
       var final = []
       for (var i in word) {
           var status = "gray"
           if (word[i] == target[i]) {
               status = "green"
               target = target.replace(word[i], " ")
           } else if (target.includes(word[i])) {
               status = "yellow"
               target = target.replace(word[i], " ")
           } 
           console.log(target);
           final.push({result: word[i], status: status});
          
       }
       console.log(prevHistory);
       setHistory([...prevHistory, final])
       prevHistory.push(final)
       setCurrentRow([])
       prevRow = []
       if (final.every((value) => value.status == "green")) {
        window.removeEventListener('keyup', checkKey)
        prevHistory = [];
        prevRow = [];
       
     }
        } 
        else if (e.keyCode == 8) {
            const tmp = prevRow.slice(0, prevRow.length - 1)
            setCurrentRow(tmp)
            prevRow = tmp;

        }
    } else if (e.keyCode == 8) {
        const tmp = prevRow.slice(0, prevRow.length - 1)
        setCurrentRow(tmp)
        prevRow = tmp;
     
            
    } else if (e.keyCode >= 65 && e.keyCode <= 90) {
        setCurrentRow([...prevRow, e.key])
        prevRow.push(e.key)
       
    }
}
    useEffect(() => {
        window.addEventListener('keyup', checkKey);
        testRef.current.focus()
        return () => {
            console.log("unmounted");
            window.removeEventListener('keyup', checkKey);
        }

    }, [])

    useEffect(() => {
        setHistory([])
        setCurrentRow([])
        setTimeout(() => {
            window.addEventListener('keyup', checkKey);
            testRef.current.focus()
            setCounter(counter + 2);
        }, 100)
       
       
       
    }, [reload])


  return (<div className="board-container">
        <HistoryRow history={history}></HistoryRow>
       {history.length < 6 && <div className="board-container"> <CurrentRow currentRow={currentRow}></CurrentRow>
        <BlankRows blankRows={6 - history.length - 1}></BlankRows></div>}
        
    
      <button onClick={(e) => {e.preventDefault(); setReload(reload + 1)}}>Click</button>
      <button onClick={(e) => {e.preventDefault(); console.log(prevHistory)}}>Cdddlick</button>
      <input className="fallback-input" ref={testRef}></input>
      <span>{currentRow}</span>
     
    
      
      
      

  </div>)
}

export default Board;
