import React from 'react';
import HistoryRow from "./HistoryRow";
import {useState, useEffect, useRef} from 'react'
import CurrentRow from "./CurrentRow";
import BlankRows from "./BlankRows";
import {VALIDGUESSES, WORDS} from '../constants.js'
function Board({wordle, client, players, user, gameEnd}) {
   const [history, setHistory] = useState([])
   const [currentRow, setCurrentRow] = useState([]);
   const [reload, setReload] = useState(0)
   const [error, setError] = useState(false)
   const [winner, setWinner] = useState(false);
   const [timer, setTimer] = useState(0)
   
   
  
   const testRef = useRef(null)

   
   var prevRow = []
   var prevHistory = []
   prevHistory = [];

   const opponent = players.find((player) => player !== user)

   function checkKey(e) {
    if (prevHistory.length == 6 || gameEnd ) return;
    if (prevRow.length == 5) {
        if (e.keyCode == 13) {
            
            var word = prevRow

            console.log(word);
            if (!VALIDGUESSES.includes(word.join('')) && !WORDS.includes(word.join(''))) {
                setError(true);
                setTimeout(() => {
                    setError(false)
                }, 2000)
                return 
            }
       var target = wordle;
       var final = []
       for (var i in word) {
           var status = "gray"
           if (word[i] == target[i]) {
               status = "green"
               target = target.replace(word[i], " ")
           } else if (target.includes(word[i])) {
               status = "yellow"
               target = target.replace(word[i], " ")
           } 
           final.push({result: word[i], status: status});
          
       }
       setHistory([...prevHistory, final])
       prevHistory.push(final)
       setCurrentRow([])
       prevRow = []
       if (final.every((value) => value.status == "green")) {
        client.send(JSON.stringify({message: "your mum won bro"}))
        setWinner(true);
       
        gameEnd = true;
        prevHistory = [];
        prevRow = [];
       
     }
        } 
        else if (e.keyCode == 8) {
            const tmp = prevRow.slice(0, prevRow.length - 1)
            setCurrentRow(tmp)
            prevRow = tmp;

        }
    } else if (e.keyCode == 8) {
        const tmp = prevRow.slice(0, prevRow.length - 1)
        setCurrentRow(tmp)
        prevRow = tmp;
     
            
    } else if (e.keyCode >= 65 && e.keyCode <= 90) {
        setCurrentRow([...prevRow, e.key])
        prevRow.push(e.key)
       
    }
}
    useEffect(() => {
        const idx = Math.floor((Math.random() * 2314) + 1);
        window.addEventListener('keyup', checkKey);
        testRef.current.focus()
        setTimeout(() => {
            setTimer(timer + 1)
        }, 1000)
        return () => {
            window.removeEventListener('keyup', checkKey);
        }

        

    }, [])

    useEffect(() => {
        setHistory([])
        setCurrentRow([])
        setTimeout(() => {
            window.addEventListener('keyup', checkKey);
            testRef.current.focus()
        }, 100)
    }, [reload])


  return (<div className="board-container">
      <div className="toasty">
      <div className={error ? "toast-container error" : "toast-container"}>Not in word list!</div>
      <div className={winner ? "toast-container error" : "toast-container"}>You won!</div>
      </div>
     
   
      {user}
        {history.length > 0 && <HistoryRow history={history}></HistoryRow>}
       {history.length < 6 && <div className="currentrow-container"> <CurrentRow currentRow={currentRow}></CurrentRow>
        <BlankRows blankRows={6 - history.length - 1}></BlankRows></div>}
        playing against: {opponent}
 
     
      <input className="fallback-input" ref={testRef}></input>

  </div>)
}

export default Board;
