import './styles.css';
import Board from "./components/Board";
import {WORDS} from './constants.js';
import {useEffect, useState, useContext} from 'react';
import {w3cwebsocket as W3CWebSocket} from "websocket";
import {GameContext} from './context/GameContext.js'
import JoinRoom from "./components/JoinRoom";
import Wrapper from "./components/Wrapper";
const client = new W3CWebSocket('ws://localhost:5000');
function App() {

  const {gameID, gameStatus, setGameStatus} = useContext(GameContext)

  return (
    <div className="App">
     {gameStatus == "waiting" || gameStatus == "connected" && <Wrapper gameID={gameID} client={client} ></Wrapper>}
     {gameStatus == "none" && <JoinRoom></JoinRoom>}
    </div>
  );
}

export default App;
