import React, { useEffect } from 'react';
import { GameContext } from "../context/GameContext";
import {useContext, useState} from 'react'
function JoinRoom({client}) {
  const {setGameID, clientID, gameID} = useContext(GameContext);
  const [createJoin, setCreateJoin] = useState(null)

  const handleCreateRoom = (e) => {
    e.preventDefault()
    const data = {
      type: "create",
      payload: {clientID: clientID}
    }
    client.send(JSON.stringify(data))
    
  }

  useEffect(() => {
    client.onmessage = (message) => {
      const data = JSON.parse(message.data);
      if (data.type == "create") {
        setGameID(data.gameID);
        setCreateJoin("create");
      }
    }
  }, [])

  return <div className="joinroom-container">
    <h2>Multiplayer Wordle by Si Wei</h2>
    <div className="option-container">
      {!createJoin && <div className="buttons">
          <button onClick={(e) => {e.preventDefault(); setCreateJoin("create")}}>Create Room</button>
          <button onClick={(e) => {e.preventDefault(); setCreateJoin("join")}}>Join Room</button>
        </div>}
      {createJoin == "create" && <div>
          {gameID}
        </div>}
    </div>
  </div>;
}

export default JoinRoom;
