import React from 'react';
import HistoryRow from "./HistoryRow";
import {useState, useEffect, useCallback, useReducer} from 'react'
import CurrentRow from "./CurrentRow";
import BlankRows from "./BlankRows";
import {initialState , actionTypes} from '../constants.js';

function Board() {

    // function reducer(state, action) {
    //     switch(action.type) {
    //         case actionTypes.TYPE_LETTER: 
    //             return {
    //                 ...state,
    //                 currentRow: [...state.currentRow, action.letter],
    //             }
    //         case actionTypes.REMOVE_LETTER:
    //             return {
    //                 ...state,
    //                 currentRow: state.currentRow.slice(0, state.currentRow.length - 1),
    //             }
            
                
            
    //     }

    // }
   
   const [history, setHistory] = useState([["a", "b", "c", "d", "e"], ["g", "h", "q", "r", "o"]])
   const [currentRow, setCurrentRow] = useState([]);
//    const [state, dispatch] = useReducer(reducer, initialState)
   var prevRow = currentRow
   var prevHistory = history

   

 

   const checkKey = (e) => {
    if (history.length == 6) return;
    if (prevRow.length == 5) {
        if (e.keyCode == 13) {
            setHistory([...prevHistory, prevRow])
            setCurrentRow([]);
            prevHistory.push(prevRow);
            prevRow = []
            
        } 
        else if (e.keyCode == 8) {
            const tmp = prevRow.slice(0, prevRow.length - 1)
            setCurrentRow(tmp)
            prevRow = tmp;
            // dispatch({type: actionTypes.REMOVE_LETTER})
           

        }
    } else if (e.keyCode == 8) {
        const tmp = prevRow.slice(0, prevRow.length - 1)
            setCurrentRow(tmp)
            prevRow = tmp;
        // dispatch({type: actionTypes.REMOVE_LETTER})
            
    } else if (e.keyCode >= 65 && e.keyCode <= 90) {
        
        setCurrentRow([...prevRow, e.key])
        prevRow.push(e.key)
        // dispatch({type: actionTypes.TYPE_LETTER, letter: e.key})
       
    }
}
    useEffect(() => {
        window.addEventListener('keyup', checkKey);
        return () => {
            window.removeEventListener('keyup', checkKey);
        }

    }, [])


  return (<div className="board-container">
        <HistoryRow history={history}></HistoryRow>
       {history.length < 6 && <div className="board-container"> <CurrentRow currentRow={currentRow}></CurrentRow>
        <BlankRows blankRows={6 - history.length - 1}></BlankRows></div>}
        
    
      <button onClick={(e) => {e.preventDefault(); console.log(currentRow)}}>Click</button>
      <span>{currentRow}</span>
    
      
      
      

  </div>)
}

export default Board;
