import React from 'react';
import HistoryRow from "./HistoryRow";

import {useState, useEffect, useCallback} from 'react'
import CurrentRow from "./CurrentRow";
function Board() {
   
   
   const [history, setHistory] = useState([["a", "b", "c", "d", "e"], ["g", "h", "q", "r", "o"]])
   const [currentRow, setCurrentRow] = useState(["d", "g"]);
   const [blankRows, setBlankRows] = useState(3)
   const [counter, setCounter] = useState(0)
   const checkKey = (e) => {
    if (currentRow.length == 5) return;
    console.log(e.keyCode);
    if (e.keyCode >= 65 && e.keyCode <= 90) {
        const tmp = currentRow;
        tmp.push(e.key)
        setCurrentRow(tmp)
        console.log(currentRow);
        setCounter(counter + 1);    
    }
}
    useEffect(() => {
        window.addEventListener('keyup', checkKey);
        return () => {
            window.removeEventListener('keyup', checkKey);
        }

    }, [])


  return (<div className="board-container">
      <HistoryRow history={history}></HistoryRow>
      
        <CurrentRow currentRow={currentRow}></CurrentRow>
        {Array(5 - currentRow.length).fill().map((letter) => {
            return (
                <div className="letter-box"><span>{letter}</span></div>
            )
        })}
        
      
      {Array(blankRows).fill(Array(5).fill()).map((row) => {
          return <div className="row-container">
              {row.map((letter) => {
                  
                  return (
                      <div className="letter-box">
                          <span>{letter}</span>
                      </div>
                  )
              })}
          </div>

      })}
    
      
      
      

  </div>)
}

export default Board;
