import './styles.css';
import Board from "./components/Board";
import {WORDS} from './constants.js';
import {useEffect, useState} from 'react';
import {w3cwebsocket as W3CWebSocket} from "websocket";


function App() {
  const [message, setMessage] = useState(null)
  const idx = Math.floor((Math.random() * 2314) + 1);
  const client = new W3CWebSocket('ws://localhost:5000');

  useEffect(() => {
    // client.onopen = () => {
    //   console.log("Client connected to ws server!");
    // }
    // client.onmessage = (message) => {
    //   console.log(message);
    // }
    client.onopen = () => {
      console.log("client connected to server");
    }
    client.onmessage = (message) => {
      console.log(message.data);
    } 

  }, [])

  const sendMessage = (e) => {
    e.preventDefault()
    client.send(
    JSON.stringify({type: "Hi"}))

  }





  return (
    <div className="App">
      {message}
     <Board wordle={WORDS[idx]} ></Board>
     <button onClick={(e)=> sendMessage(e)}>hi</button>
    </div>
  );
}

export default App;
