import React from 'react';
import {useEffect, useState, useContext} from 'react';
import Board from "./Board";
import {GameContext} from '../context/GameContext.js'
import {w3cwebsocket as W3CWebSocket} from "websocket";
import JoinRoom from "./JoinRoom";
function Wrapper({client}) {
    const {gameID, gameStatus, setGameStatus, setClientID, setCreateJoin, setGameID, currentName, setCurrentName} = useContext(GameContext);
    const [wordle, setWordle] = useState(null)
    const [error, setError] = useState(null)
    const [players, setPlayers] = useState(null)
    const [gameEnd, setGameEnd] = useState(false)
    const [reload, setReload] = useState(0);

    useEffect(() => {
        client.onopen = () => {
          console.log("client connected to server");
        }
        client.onmessage = (message) => {
            const data = JSON.parse(message.data);
            console.log(data);
            if (data.type == "initial") {
              setClientID(data.clientID)
              console.log(data.clientID);
            }
      
            if (data.type == "create") {
              setGameID(data.gameID);
              setCreateJoin("create");
            }

            if (data.type == "join-error") {
                if (data.message == "full") {
                    setError('Room is full');
                } else {
                    setError ('Invite code is invalid');
                }
            }
            if (data.type == "join-success") {
                setGameID(data.gameID);
                setError(null);

            }
            if (data.type == "start") {
                setWordle(data.wordle)
                setGameID(data.gameID)
                setPlayers(data.players)
                setGameStatus("connected")
                
                
            }
            if (data.type == "winner") {
                
                setGameEnd({status: true, winner: data.winner});
            }
          }
        
      }, [])

    useEffect(() => {
        console.log("Reloaded")
    }, [gameStatus])

    const renderSwitch = (gameStatus) => {
        switch(gameStatus) {
            case("waiting"):
                return (<div>Waiting for other player to connect...</div>);
            case("connected"):
                return (<Board reload={reload} gameID={gameID} client={client} wordle={wordle} players={players} user={currentName} gameEnd={gameEnd}></Board>);
            case("none"):
                return (<JoinRoom error={error} client={client}></JoinRoom>)
        }
    }


  return <div>{renderSwitch(gameStatus)}</div>
}

export default Wrapper;
