import React from 'react';
import HistoryRow from "./HistoryRow";
import {useState, useEffect, useRef} from 'react'
import CurrentRow from "./CurrentRow";
import BlankRows from "./BlankRows";
import {VALIDGUESSES, WORDS} from '../constants.js'
import Opponent from "./Opponent";
import Keyboard from "./Keyboard";
import {keyrow1, keyrow2, keyrow3} from '../constants.js';
function Board({gameID, wordle, client, players, user, gameEnd, reload, clientID, opponentData}) {
   const [history, setHistory] = useState([])
   const [currentRow, setCurrentRow] = useState([]);
   const [error, setError] = useState(false)
   const [winner, setWinner] = useState(null);
   const [timer, setTimer] = useState(0)
   const [keyboard, setKeyboard] = useState([keyrow1, keyrow2, keyrow3])

   useEffect(() => {
       const initKeys = [keyrow1, keyrow2, keyrow3];
       setKeyboard(initKeys);
       console.log(initKeys);
    
   }, [])

   useEffect(() => {
    var initKeys = [keyrow1, keyrow2, keyrow3];
    for (var a in history) {
        for (var c in history[a]) {
            var flag = false
            if (flag) break;
            else {
                for (const j in initKeys) {
                    for (var b in initKeys[j]) {
                        if (history[a][c].key == initKeys[j][b].key) {
                            initKeys[j][b].status = history[a][c].status
                            flag = true;
                            break;
                        }
                    }
                }
                
            }
            
        }

        
    }
    setKeyboard(initKeys)
}, [history])
   
   const testRef = useRef(null)

   console.log(clientID);
   var prevRow = []
   var prevHistory = []
   prevHistory = [];

   

   function checkKey(e) {
      
    if (prevHistory.length == 6 || gameEnd.status ) return;
    if (prevRow.length == 5) {
        if (e.keyCode == 13) {
            
            var word = prevRow

            console.log(word);
            if (!VALIDGUESSES.includes(word.join('')) && !WORDS.includes(word.join(''))) {
                setError(true);
                setTimeout(() => {
                    setError(false)
                }, 2000)
                return 
            }
       var target = wordle;
       var final = []
       for (var i in word) {
           var status = "gray"
           if (word[i] == target[i]) {
               status = "green"
               target = target.replace(word[i], " ")
           } else if (target.includes(word[i])) {
               status = "yellow"
               target = target.replace(word[i], " ")
           } 
           final.push({key: word[i], status: status});
          
       }
       setHistory([...prevHistory, final])
       prevHistory.push(final)
       client.send(JSON.stringify({type: "update", payload: {rows: prevHistory, clientID: clientID, gameID: gameID}}));
       setCurrentRow([])
       prevRow = []
       if (final.every((value) => value.status == "green")) {
        const data = {
            type: "winner",
            payload: {
                winner: user,
                gameID: gameID
                }
        }
        client.send(JSON.stringify(data));
        setWinner(true);
        prevHistory = [];
        prevRow = [];
       
     }
        } 
        else if (e.keyCode == 8) {
            const tmp = prevRow.slice(0, prevRow.length - 1)
            setCurrentRow(tmp)
            prevRow = tmp;

        }
    } else if (e.keyCode == 8) {
        const tmp = prevRow.slice(0, prevRow.length - 1)
        setCurrentRow(tmp)
        prevRow = tmp;
     
            
    } else if (e.keyCode >= 65 && e.keyCode <= 90) {
        setCurrentRow([...prevRow, e.key])
        prevRow.push(e.key)
       
    }
}
    useEffect(() => {
        console.log("did this");
        window.addEventListener('keyup', checkKey);
        testRef.current.focus()
        setTimeout(() => {
            setTimer(timer + 1)
        }, 1000)
        return () => {
            window.removeEventListener('keyup', checkKey);
        }
    }, [])

    useEffect(()=>{
        if (gameEnd.status) {
            window.addEventListener('keyup', function(event) {
                event.stopImmediatePropagation();
            }, true);
            if (gameEnd.winner == user) {
                setWinner(true)
                window.removeEventListener('keyup', checkKey);
                
            } else {
                setWinner(false);
                window.removeEventListener('keyup', checkKey);
            }
        }
        return () => {
            window.removeEventListener('keyup', function(event) {
                event.stopImmediatePropagation();
            }, true);
        }

    }, [reload])

    


  return (<div className="board-container">
       <div className="toasty">
      <div className={error ? "toast-container error" : "toast-container"}>Not in word list!</div>
      <div className={winner ? "toast-container error" : "toast-container"}>You won!</div>
      <div className={winner == false ? "toast-container error" : "toast-container"}>You lost!</div>
      </div>
      <Opponent opponentData={opponentData} opponent={players.find((player) => player !== user)}></Opponent>
     
     
   
      <p>{user}</p>
        {history.length > 0 && <HistoryRow history={history} opponent={"false"}></HistoryRow>}
       {history.length < 6 && <div className="currentrow-container"> <CurrentRow currentRow={currentRow}></CurrentRow>
        <BlankRows opponent={"false"} blankRows={6 - history.length - 1}></BlankRows></div>}
      <input className="fallback-input" ref={testRef}></input>
      <div className="KEYBOARD">
      <div className="keyrow one">
          {keyboard[0].map((key) => {
              return (
                  <div className={`keytile ${key.status}`}>
                      <span id="keyboardtile">{key.key.toUpperCase()}</span>
                  </div>
              )
          })}
      </div>
  </div>

  </div>)
}

export default Board;
