import React from 'react';
import HistoryRow from "./HistoryRow";
import {useState, useEffect, useRef} from 'react'
import CurrentRow from "./CurrentRow";
import BlankRows from "./BlankRows";
import {VALIDGUESSES, WORDS} from '../constants.js'
function Board({gameID, wordle, client, players, user, gameEnd, reload}) {
   const [history, setHistory] = useState([])
   const [currentRow, setCurrentRow] = useState([]);
   const [error, setError] = useState(false)
   const [winner, setWinner] = useState(null);
   const [timer, setTimer] = useState(0)
   
   const testRef = useRef(null)

   
   var prevRow = []
   var prevHistory = []
   prevHistory = [];

   const opponent = players.find((player) => player !== user)

   function checkKey(e) {
       console.log(gameEnd.status);
    if (prevHistory.length == 6 || gameEnd.status ) return;
    if (prevRow.length == 5) {
        if (e.keyCode == 13) {
            
            var word = prevRow

            console.log(word);
            if (!VALIDGUESSES.includes(word.join('')) && !WORDS.includes(word.join(''))) {
                setError(true);
                setTimeout(() => {
                    setError(false)
                }, 2000)
                return 
            }
       var target = wordle;
       var final = []
       for (var i in word) {
           var status = "gray"
           if (word[i] == target[i]) {
               status = "green"
               target = target.replace(word[i], " ")
           } else if (target.includes(word[i])) {
               status = "yellow"
               target = target.replace(word[i], " ")
           } 
           final.push({result: word[i], status: status});
          
       }
       setHistory([...prevHistory, final])
       prevHistory.push(final)
       setCurrentRow([])
       prevRow = []
       if (final.every((value) => value.status == "green")) {
        const data = {
            type: "winner",
            payload: {
                winner: user,
                gameID: gameID
                }
        }
        client.send(JSON.stringify(data));
        setWinner(true);
        prevHistory = [];
        prevRow = [];
       
     }
        } 
        else if (e.keyCode == 8) {
            const tmp = prevRow.slice(0, prevRow.length - 1)
            setCurrentRow(tmp)
            prevRow = tmp;

        }
    } else if (e.keyCode == 8) {
        const tmp = prevRow.slice(0, prevRow.length - 1)
        setCurrentRow(tmp)
        prevRow = tmp;
     
            
    } else if (e.keyCode >= 65 && e.keyCode <= 90) {
        setCurrentRow([...prevRow, e.key])
        prevRow.push(e.key)
       
    }
}
    useEffect(() => {
        console.log("did this");
        client.onmessage = (message) => {
            console.log(message);
        }
        const idx = Math.floor((Math.random() * 2314) + 1);
        window.addEventListener('keyup', checkKey);
        testRef.current.focus()
        setTimeout(() => {
            setTimer(timer + 1)
        }, 1000)
        return () => {
            window.removeEventListener('keyup', checkKey);
        }
    }, [])

    useEffect(()=>{
        if (gameEnd.status) {
            window.addEventListener('keyup', function(event) {
                event.stopImmediatePropagation();
            }, true);
            if (gameEnd.winner == user) {
                setWinner(true)
                window.removeEventListener('keyup', checkKey);
                
            } else {
                setWinner(false);
                window.removeEventListener('keyup', checkKey);
            }
        }
        return () => {
            window.removeEventListener('keyup', function(event) {
                event.stopImmediatePropagation();
            }, true);
        }

    }, [reload])

    


  return (<div className="board-container">
      <div className="toasty">
      <div className={error ? "toast-container error" : "toast-container"}>Not in word list!</div>
      <div className={winner ? "toast-container error" : "toast-container"}>You won!</div>
      <div className={winner == false ? "toast-container error" : "toast-container"}>You lost!</div>
      </div>
     
   
      {user}
        {history.length > 0 && <HistoryRow history={history}></HistoryRow>}
       {history.length < 6 && <div className="currentrow-container"> <CurrentRow currentRow={currentRow}></CurrentRow>
        <BlankRows blankRows={6 - history.length - 1}></BlankRows></div>}
        playing aagainst: {opponent}
 
     
      <input className="fallback-input" ref={testRef}></input>

  </div>)
}

export default Board;
