import React from 'react';
import { GameContext } from "../context/GameContext";
import {useContext, useState} from 'react'
function JoinRoom({client}) {
  const {setGameID} = useContext(GameContext);
  const [createJoin, setCreateJoin] = useState(null)

  return <div className="joinroom-container">
    <h2>Multiplayer Wordle by Si Wei</h2>
    <div className="option-container">
      {!createJoin && <div className="buttons">
          <button>Create Room</button>
          <button>Join Room</button>
        </div>}
    </div>
  </div>;
}

export default JoinRoom;
