import React from 'react';
import {useState, useEffect, useCallback} from 'react'
function Board() {
   const checkKey = (e) => {
       console.log(e.key);
   }
   const [history, setHistory] = useState([["a", "b", "c", "d", "e"], ["g", "h", "q", "r", "o"]])
   const [currentRow, setCurrentRow] = useState(["d", "g"]);
   const [blankRows, setBlankRows] = useState(6)
    useEffect(() => {
        window.addEventListener('keyup', checkKey);
        return () => {
            window.removeEventListener('keyup', checkKey);
        }

    }, [])


  return (<div className="board-container">
      {history.map((row) => {
          return (
              <div className="row-container">
                  {row.map((letter) => {
                      return (
                          <div className="letter-box"><span>{letter}</span></div>
                      )
                  })}
              </div>
          )
      })}
      {currentRow.map((row) => {
          return (
              <div className="row-container">
                  {row.map((letter) => {
                      return (
                          <div className="letter-box"><span>{letter}</span></div>
                      )
                  })}
              </div>
          )
      })}
      
      
      

  </div>)
}

export default Board;
