import React from 'react';
import HistoryRow from "./HistoryRow";
import {useState, useEffect, useRef, useReducer, useCallback} from 'react'
import CurrentRow from "./CurrentRow";
import BlankRows from "./BlankRows";
import {VALIDGUESSES, WORDS} from '../constants.js'
import Opponent from "./Opponent";
import Keyboard from "./Keyboard";
import {keyrow1, keyrow2, keyrow3} from '../constants.js';
const actionTypes = {
    TYPE_LETTER: "TYPE_LETTER",
    TYPE_BACKSPACE: "TYPE_BACKSPACE",
    ACCEPT_RESPONSE: "ACCEPT_RESPONSE",
    ACCEPT_ERROR: "ACCEPT_ERROR",
  };

  const initialState = {
    currentRow: [],
    history: [],
  };

// function reducer(state, action) {
//     switch(action.type) {
//         case actionTypes.TYPE_LETTER:
//             return {
//                 ...state,
//                 currentRow: [...state.currentRow, action.payload]
//             }
//         case actionTypes.TYPE_BACKSPACE:
//             return {
//                 ...state,
//                 currentRow: state.currentRow.slice(0, state.currentRow.length - 1)
//             }
//         case actionTypes.ACCEPT_RESPONSE: 
//             return {
//                 history: [...state.history, action.payload],
//                 currentRow: [],
//             }
//         default:
//             throw new Error();
//     }
// }
function Board({gameID, wordle, client, players, user, gameEnd, reload, clientID, opponentData}) {
   
   
   const [error, setError] = useState(false)
   const [winner, setWinner] = useState(null);
   const [currentRow, _setCurrentRow] = useState([])
   const [history, _setHistory] = useState([])
   const [keyboard, setKeyboard] = useState([keyrow1, keyrow2, keyrow3])
   const currentRowRef = useRef(currentRow)
   const historyRef = useRef(history);
   const testRef = useRef(null)

   const setHistory = x => {
       historyRef.current = x;
       _setHistory(x);
   }
   const setCurrentRow = x => {
       currentRowRef.current = x;
       _setCurrentRow(x);
   }

//    const [state, dispatch] = useReducer(reducer, initialState);
//    const {currentRow, history} = state


   useEffect(() => {
       const initKeys = [keyrow1, keyrow2, keyrow3];
       setKeyboard(initKeys);
       console.log(initKeys);
    
   }, [])

   useEffect(() => {
    var initKeys = [keyrow1, keyrow2, keyrow3];
    for (var a in history) {
        for (var c in history[a]) {
            var flag = false
            if (flag) break;
            else {
                for (const j in initKeys) {
                    for (var b in initKeys[j]) {
                        if (history[a][c].key == initKeys[j][b].key) {
                            initKeys[j][b].status = history[a][c].status
                            flag = true;
                            break;
                        }
                    }
                }
                
            }
            
        }

        
    }
    setKeyboard(initKeys)
}, [history])
   

   console.log(clientID);
   var prevRow = []
   var prevHistory = []
   prevHistory = [];

   
   
   function checkKey(e) {
    console.log(prevRow);
    if (historyRef.current.length == 6 || gameEnd.status ) return;
    console.log(e.keyCode);

    
    if (currentRowRef.current.length == 5) {
        
        if (e.keyCode == 13) {
            console.log("entered");
            var word = currentRowRef.current

            console.log(word);
            if (!VALIDGUESSES.includes(word.join('')) && !WORDS.includes(word.join(''))) {
                setError(true);
                setTimeout(() => {
                    setError(false)
                }, 2000)
                return 
            }
       var target = wordle;
       var final = []
       for (var i in word) {
           var status = "gray"
           if (word[i] == target[i]) {
               status = "green"
               target = target.replace(word[i], " ")
           } else if (target.includes(word[i])) {
               status = "yellow"
               target = target.replace(word[i], " ")
           } 
           final.push({key: word[i], status: status});
          
       }
       setHistory([...historyRef.current, final])
       setCurrentRow([])
       prevHistory.push(final)
    //    dispatch({type: actionTypes.ACCEPT_RESPONSE, payload: final})
       client.send(JSON.stringify({type: "update", payload: {rows: historyRef.current, clientID: clientID, gameID: gameID}}));
    //    setCurrentRow([])
       prevRow = []
       if (final.every((value) => value.status == "green")) {
        const data = {
            type: "winner",
            payload: {
                winner: user,
                gameID: gameID
                }
        }
        client.send(JSON.stringify(data));
        setWinner(true);
        // prevHistory = [];
        // prevRow = [];
       
     }
        } 
        else if (e.keyCode == 8) {
            const tmp = prevRow.slice(0, prevRow.length - 1)
            setCurrentRow(tmp)
            prevRow = tmp;
            // dispatch({type: actionTypes.TYPE_BACKSPACE})

        }
    } else if (e.keyCode == 8) {
        const tmp = prevRow.slice(0, prevRow.length - 1)
        setCurrentRow(tmp)
        prevRow = tmp;
        // dispatch({type: actionTypes.TYPE_BACKSPACE})
     
            
    } else if (currentRowRef.current.length < 5) {
        if (e.keyCode >= 65 && e.keyCode <= 90) {
            setCurrentRow([...currentRowRef.current, e.key])
            prevRow.push(e.key)
            // dispatch({type: actionTypes.TYPE_LETTER, payload: e.key});
           
        }

    }
}

    const handleKeyChange = (d) => {
        console.log(prevRow);
        prevRow = currentRow;
        prevHistory = history;
        d.preventDefault();
        const key = d.target.getAttribute("data")
        const e = d.target.getAttribute("data").charCodeAt(0);
        console.log(e);
        if (historyRef.current.length == 6 || gameEnd.status) return;

        if (currentRowRef.current.length == 5) {
            if (e == 69) {
                
                var word = prevRow
    
                console.log(word);
                if (!VALIDGUESSES.includes(word.join('')) && !WORDS.includes(word.join(''))) {
                    setError(true);
                    setTimeout(() => {
                        setError(false)
                    }, 2000)
                    return 
                }
           var target = wordle;
           var final = []
           for (var i in word) {
               var status = "gray"
               if (word[i] == target[i]) {
                   status = "green"
                   target = target.replace(word[i], " ")
               } else if (target.includes(word[i])) {
                   status = "yellow"
                   target = target.replace(word[i], " ")
               } 
               final.push({key: word[i], status: status});
              
           }
           setHistory([...historyRef.current , final])
           prevHistory.push(final)
           client.send(JSON.stringify({type: "update", payload: {rows: prevHistory, clientID: clientID, gameID: gameID}}));
           setCurrentRow([])
           prevRow = []
           if (final.every((value) => value.status == "green")) {
            const data = {
                type: "winner",
                payload: {
                    winner: user,
                    gameID: gameID
                    }
            }
            client.send(JSON.stringify(data));
            setWinner(true);
            prevHistory = [];
            prevRow = [];
           
         }
            } 
            else if (e == 68) {
                const tmp = prevRow.slice(0, currentRowRef.current.length - 1)
                setCurrentRow(tmp)
                prevRow = tmp;
    
            }
        } else if (e == 68) {
            const tmp = prevRow.slice(0, currentRowRef.current.length - 1)
            setCurrentRow(tmp)
            prevRow = tmp;
         
                
        } else if (e >= 97 && e <= 122) {
            setCurrentRow([...currentRowRef.current, key])
            prevRow.push(key)
           
        }


    }
    useEffect(() => {
        console.log("did this");
        window.addEventListener('keyup', checkKey);
        testRef.current.focus()
        return () => {
            window.removeEventListener('keyup', checkKey)
        }
    }, [])

    useEffect(()=>{
        if (gameEnd.status) {
            window.addEventListener('keyup', function(event) {
                event.stopImmediatePropagation();
            }, true);
            if (gameEnd.winner == user) {
                setWinner(true)
                window.removeEventListener('keyup', checkKey);
                
            } else {
                setWinner(false);
                window.removeEventListener('keyup', checkKey);
            }
        }
        return () => {
            window.removeEventListener('keyup', function(event) {
                event.stopImmediatePropagation();
            }, true);
        }

    }, [reload])

    


  return (<div className="board-container">
    <Opponent opponentData={opponentData} opponent={players.find((player) => player !== user)}></Opponent>
    <div className="user-container">
        <div className="toasty">
            <div className={error ? "toast-container error" : "toast-container"}>Not in word list!</div>
            <div className={winner ? "toast-container error" : "toast-container"}>You won!</div>
            <div className={winner == false ? "toast-container error" : "toast-container"}>You lost!</div>
            <div className={winner !== null ? "toast-container rematch" : "toast-container"}>Rematch</div>
        </div>
      <p>{user}</p>
        {history.length > 0 && <HistoryRow history={historyRef.current} opponent={"false"}></HistoryRow>}
        {history.length < 6 && <div className="currentrow-container"> <CurrentRow currentRow={currentRowRef.current}></CurrentRow>
        <BlankRows opponent={"false"} blankRows={6 - historyRef.current.length - 1}></BlankRows></div>}
      <input className="fallback-input" ref={testRef}></input>
      </div> 
    <div className="keyboard">
        <div className="keyrow one">
            {keyboard[0].map((key) => {
                return (
                    <div  value={key.key} className={`keytile ${key.status}`}>
                        <button onClick={(e) => handleKeyChange(e)} data={key.key} className={`keyboardtile ${key.status}`}>{key.key.toUpperCase()}</button>
                    </div>
                )
            })}
        </div>
        <div className="keyrow two">
            {keyboard[1].map((key) => {
                return (
                    <div  value={key.key} className={`keytile ${key.status}`}>
                        <button onClick={(e) => handleKeyChange(e)} data={key.key} className={`keyboardtile ${key.status}`}>{key.key.toUpperCase()}</button>
                    </div>
                )
            })}
        </div>
        <div className="keyrow three">
            <div className="keytile none special"  value={"ENTER"}>
                        <button onClick={(e) => handleKeyChange(e)} data={"ENTER"} className={`keyboardtile`}>ENTER</button>
                    </div>
            {keyboard[2].map((key) => {
                
                return (
                    <div value={key.key}  className={`keytile ${key.status}`}>
                        <button  onClick={(e) => handleKeyChange(e)} data={key.key} className={`keyboardtile ${key.status}`}>{key.key.toUpperCase()}</button>
                    </div>
                )
            })}
            <div value={"DELETE"} className="keytile none special">
            <button onClick={(e) => handleKeyChange(e)} data={"DELETE"} className={`keyboardtile`}>DELETE</button>
         </div>
    </div>
</div>

  </div>)
}

export default Board;
