                console.log("final init");
            }, 100)
        }
   
       
    // }, true);
    console.log("did this");
    setHistory([])
    setCurrentRow([]);
    setWinner(null);
    window.addEventListener('keyup', middlewareKey);
    testRef.current.focus()
    return () => {
        window.removeEventListener('keyup', middlewareKey)
    }
}, [wordle])

