            console.log(history[a][c]);
            var flag = false
            if (flag) break;
            else {
                for (const j in initKeysOne) {
                    for (var b in initKeysOne[j]) {
                        if (history[a][c].key == initKeysOne[j][b].key) {
                            initKeysOne[j][b].status = history[a][c].status
                            flag = true;
                            break;
                        }
                    }
                }
                
            }
            
        }

        
    }
    setKeyboard(initKeysOne)

    }
    
}, [history])
   

   console.log(clientID);

   const handleRematch = e => {
       e.preventDefault();
       const data = {
           type: "rematch",
           payload: {
                gameID: gameID,
                clientID: clientID,
           }
           
       }
       client.send(JSON.stringify(data));
       setRematch(false);
      
       
