import React from 'react';
import HistoryRow from "./HistoryRow";
import {useState, useEffect, useCallback} from 'react'
import CurrentRow from "./CurrentRow";
import BlankRows from "./BlankRows";
function Board() {
   
   
   const [history, setHistory] = useState([["a", "b", "c", "d", "e"], ["g", "h", "q", "r", "o"]])
   const [currentRow, setCurrentRow] = useState([]);
   const [blankRows, setBlankRows] = useState(3)
   var prevRow = currentRow
   var prevHistory = history

   const checkKey = (e) => {
    if (prevRow.length == 5) {
        if (e.keyCode == 13) {
            setHistory([...prevHistory, currentRow])
            setCurrentRow([]);
            prevRow = []
            prevHistory.push(currentRow);
        } 
        else if (e.keyCode == 8) {
            const tmp = prevRow.slice(0, prevRow.length - 1)
            setCurrentRow(tmp)
            prevRow = tmp;
           

        }
    } else if (e.keyCode == 8) {
        const tmp = prevRow.slice(0, prevRow.length - 1)
            setCurrentRow(tmp)
            prevRow = tmp;
            
    } else if (e.keyCode >= 65 && e.keyCode <= 90) {
        
        setCurrentRow([...prevRow, e.key])
        prevRow.push(e.key)
       
    }
}
    useEffect(() => {
        window.addEventListener('keyup', checkKey);
        return () => {
            window.removeEventListener('keyup', checkKey);
        }

    }, [])


  return (<div className="board-container">
        <HistoryRow history={history}></HistoryRow>
        <CurrentRow currentRow={currentRow}></CurrentRow>
        <BlankRows blankRows={6 - history.length - 1}></BlankRows>
        
     
      <button onClick={(e) => {e.preventDefault(); console.log(currentRow)}}>Click</button>
      <span>{currentRow}</span>
    
      
      
      

  </div>)
}

export default Board;
